mapper.select:

		select board_no, title, post_id, write_date, read_cnt, area, is_notice, is_popular
		from (
		        select row_number() over(order by is_notice desc,  is_popular desc, like_cnt desc, write_date desc) rn, a.*
		        from (
		                select  board_no, title, post_id, write_date, read_cnt, area, is_notice, is_popular, like_cnt
                        from board
                        where board_code = 11 and is_notice = 1 and is_del = 0

		                union

		                select  board_no, title, post_id, write_date, read_cnt, area, is_notice,  is_popular, like_cnt
                        from board
                        where board_code = 11 and is_del = 0

                        union

                        select board_no, title, post_id, write_date, read_cnt, area, is_notice, is_popular, like_cnt
                        from (
                                select  rank() over(order by like_cnt desc, board_no desc) rank, board_no, title, post_id, write_date, read_cnt, area, is_notice, is_popular, like_cnt
                                from board
                                where board_code = 11 and is_del = 0
                            )
                        where rank <= 3
					)a
				where 1=1



			)b
		where rn between #{startRow} and #{endRow}



		select board_no, title, post_id, write_date, read_cnt, area, is_notice, is_popular
