select *
from map natural join board
where board_no=#{boardNo}

select *
from board_file natural join board
where board_no=#{boardNo}

mapper.update:
update board 
set area=#{boardDto.area}, title=#{boardDto.title}, content=#{boardDto.content}, update_date=sysdate, is_notice=#{boardDto.isNotice}
where board_no=#{boardDto.boardNo}

update board 
set is_del=1
where board_no=#{boardDto.boardNo}
